// Helper functions for authentication
export const signInWithEmail = async (email: string, password: string) => {
  return await supabase.auth.signInWithPassword({ email, password });
};

export const signUpWithEmail = async (
  email: string, 
  password: string, 
  metadata: Record<string, any> = {}
) => {
  // Add default subscription plan to metadata if not provided
  const enhancedMetadata = {
    ...metadata,
    subscription_plan: metadata.subscription_plan || "free",
    subscription_start_date: metadata.subscription_start_date || new Date().toISOString(),
    subscription_end_date: metadata.subscription_end_date || getDefaultEndDate()
  };
  
  return await supabase.auth.signUp({ 
    email, 
    password,
    options: { 
      data: enhancedMetadata 
    }
  });
};

// Helper function to calculate default end date (1 month from now for free plan)
const getDefaultEndDate = () => {
  const endDate = new Date();
  endDate.setMonth(endDate.getMonth() + 1); // Free plan is valid for 1 month
  return endDate.toISOString();
};

