// Helper function to get products with pagination
export const getProductsWithPagination = async (storeId: string, page: number, pageSize: number, searchQuery?: string) => {
  try {
    const startRange = page * pageSize;
    const endRange = startRange + pageSize - 1;
    
    let query = supabase
      .from('products')
      .select('*', { count: 'exact' })
      .eq('store_id', storeId)
      .range(startRange, endRange);
    
    if (searchQuery) {
      query = query.or(`name.ilike.%${searchQuery}%,description.ilike.%${searchQuery}%`);
    }
    
    const { data, error, count } = await query.order('created_at', { ascending: false });
    
    if (error) {
      console.error("Error fetching products with pagination:", error);
      return { data: null, error, count: 0 };
    }
    
    return { data, error: null, count: count || 0 };
  } catch (error) {
    console.error("Error in getProductsWithPagination function:", error);
    return { data: null, error, count: 0 };
  }
};

